        private static Dictionary<string, Category> _categories;
        public static Dictionary<string, Category> Categories
        {
            get
            {
                if (_categories == null)
                {
                    var categoryList = new[]
                    {
                        new Category { CategoryName = "Fruit pies" },
                        new Category { CategoryName = "Cheese cakes" },
                        new Category { CategoryName = "Seasonal pies" }
                    };

                    _categories = new Dictionary<string, Category>();

                    foreach (Category category in categoryList)
                    {
                        _categories.Add(category.CategoryName, category);
                    }
                }

                return _categories;
            }
        }

        public Task<IEnumerable<Product>> GetAllProductsAsync()
        {
            return Task.FromResult(mockProductCatalog);
        }

        public Task<IEnumerable<Product>> GetProductsOfTheWeekAsync()
        {
            return Task.FromResult(mockProductCatalog.Where(p => p.IsProductOfTheWeek == true));
        }
